mapper.select:
SELECT b.no, h.heroimg, m.mapname, b.title, b.`DATE`, b.hits, b.id FROM BOARD AS b inner join HERO AS h ON (b.hno = h.no) INNER JOIN MAP AS m ON (b.mno = m.`no`) WHERE 1 = 1
and b.hno = ${hno}
and b.mno = ${mno}
and b.title LIKE '$%{title}%'
and b.id LIKE '%${id}%'
limit ${position}, 4

SELECT count(*) FROM BOARD AS b inner join HERO AS h ON (b.hno = h.no) INNER JOIN MAP AS m ON (b.mno = m.`no`) WHERE 1 = 1
and b.hno = ${hno}
and b.mno = ${mno}

mapper.update:
	UPDATE BOARD
 	SET hits = hits + 1
 	WHERE BOARD.no = ${no}
